from sadie.antibody.rearrangment import VDJSequence
from pprint import pprint
from Bio.Seq import Seq

# Can we compose a VDJ Sequence Input
vdj_seq = VDJSequence(
    sequence_id="my_sequence",
    sequence="",
)
pprint(vdj_seq.__dict__)

# Can pass Seq Object too
vdj_seq = VDJSequence(
    sequence_id="my_sequence",
    sequence=Seq(
        ""
    ),
)
pprint(vdj_seq.__dict__)

# Don't provide an ID and a has will be generated
vdj_seq = VDJSequence(
    sequence_id=None,
    sequence="",
    vdj_sequence="",
    sequnce_aa="QRLVESGGGVVQPGSSLRLSCAASGFDFSRQGMHWVRQAPGQGLEWVAFIKYDGSEKYHADSVWGRLSISRDNSKDTLYLQMNSLRVEDTATYFCVREAGGPDYRNGYNYYDFYDGYYNYHYMDVWGKGTTVTVSS",
)
pprint(vdj_seq.__dict__)

# explicitly provide the aa sequence but no VDJ_seq
vdj_seq = VDJSequence(
    sequence_id=None,
    sequence="",
    sequnce_aa="QRLVESGGGVVQPGSSLRLSCAASGFDFSRQGMHWVRQAPGQGLEWVAFIKYDGSEKYHADSVWGRLSISRDNSKDTLYLQMNSLRVEDTATYFCVREAGGPDYRNGYNYYDFYDGYYNYHYMDVWGKGTTVTVSS",
)

pprint(vdj_seq.__dict__)

# explicitly provide the aa sequence as Seq
vdj_seq = VDJSequence(
    sequence_id=None,
    sequence="",
    sequnce_aa=Seq(
        "QRLVESGGGVVQPGSSLRLSCAASGFDFSRQGMHWVRQAPGQGLEWVAFIKYDGSEKYHADSVWGRLSISRDNSKDTLYLQMNSLRVEDTATYFCVREAGGPDYRNGYNYYDFYDGYYNYHYMDVWGKGTTVTVSS"
    ),
)
